import { ADD_PRODUCT } from "../constants/action-types";

const initialState = {
    products : [
        {
            name : 'Gokul',
            price : 234
        },
        {
            name : 'Shaji',
            price : 234
        }
    ]
};

function rootReducer(state = initialState, action){

    switch(action.type){

        case ADD_PRODUCT :
            return {
                ...state,
                products : [...state.products, action.payload]
            } ;

        default :
            return state ;
    }

};

export default rootReducer ;
