import React, { Component } from 'react' ;
import { selectCategory } from "../actions/index" ;
import { connect } from "react-redux" ;

function mapDispatchToProps(dispatch){
    return { selectCategory: (category) => dispatch(selectCategory(category))};
} 

class ConnectedCategory extends Component {
    constructor(props){
        super(props);
        this.state = {
            category: ""
        } ;   
    }

    handleChange = (event) => {
        this.setState({ [event.target.id] : event.target.value});
        const category  = this.state.category ;
        console.log(category)
        this.props.selectCategory(category);
    };

    // handleSubmit = (event) => {
    //     event.preventDefault() ;
    //     const newProduct  = this.state ;
    //     this.props.addProduct(newProduct) ;
    //     this.setState({
    //         name : ""
    //     });
    // };

    render(){
        const newProduct = this.state ;

        return(
            <select id = "category" onChange = {this.handleChange}> 
                <option value="" Selected>All</option>
                <option value="Mobiles">Mobiles</option>
            </select>
        );
    }
}

const SelectCategory = connect(null,mapDispatchToProps)(ConnectedCategory) ;

export default SelectCategory ;
